<template>
    <div class="item-con">
      <div class="num question-item">答题数<span class="remain">{{ problems.length-itemNum }}</span></div>
      <div class="title question-item">{{ problems[itemNum].problemTitle }} <span class="triangle"></span></div>
      <div class="question-lists">
        <li v-for="(item, key) in problems[itemNum].answers"><input v-model="chooseId" type="radio" :value="item" :id="item.answerId" name="answer"><label :for="item.answerId">{{ item.text }}</label></li>
      </div>
      <div class="btns">
        <button class="red-bg next-btn" @click="nextItem" v-if="itemNum < problems.length-1">下一题</button>
        <button class="green-bg done-btn" @click="postAll" v-else>交卷</button>
      </div>
      <modal v-if="isDone">
          <done-modal></done-modal>
      </modal>
    </div>
</template>
<script>
  import { mapState, mapActions } from 'vuex'
  import modal from '../components/modal'
  import doneModal from './doneModal'
  export default {
    components:{
      'modal': modal,
      'done-modal': doneModal
    },
    data () {
      return {
        isClose: false,
        chooseId: null,
        isDone: false,
        allScore: null,
        theScoreHeget: this.$store.getters.theScoreHeget
      }
    },
    created () {
      this.initData()
    },
    mounted () {

    },
    activated () {
//      判断是否完成答题，从首页进入取消遮罩，从排行榜返回仍显示遮罩
      if(this.$store.state.isDone){
        this.isDone = true
      }else{
        this.isDone = false
      }
    },
    computed: {
      ...mapState([
        'itemNum',
        'allTime',
        'timer',
        'problems',
        'answerId',
        'resultScore',
      ]),
    },
    methods: {
      ...mapActions([
        'initData','addItem'
      ]),
//    下一步
      nextItem () {
        if(this.chooseId){
          let obj = {}
          obj.answerId = this.chooseId.answerId
          obj.fid = this.chooseId.fid
          obj.theScoreHeget= this.$store.theScoreHeget
          this.chooseId = null
//        action提交
          this.addItem(obj)
        }else{
          alert('请选择')
        }
      },
      postAll () {
        if(this.chooseId){
          let obj = {}
          obj.answerId = this.chooseId.answerId
          obj.fid = this.chooseId.fid
          obj.theScoreHeget=this.$store.theScoreHeget
          this.chooseId = null
//        action提交
          this.addItem(obj)
//        最后一题清楚定时器
          clearInterval(this.timer)
          this.$store.commit('CLEAR_TIME')
          this.isDone = true
        }else{
          alert('请选择')
        }
      }
    }
  }
</script>

