    border-width:10px;
    border-style:solid;
    border-color: #dbdbdb transparent transparent transparent;
    position:absolute;
    bottom:-20px;
    left: 50%;
    margin-left:-10px;
  }
  .item-con .triangle:after{
    content: '';
    display:block;
    width:0;
    height:0;
    border-width: 10px;
    border-style:solid;
    border-color: #fff transparent transparent transparent;
    position:absolute;
    top:-11px;
    left:-10px;
  }
  .question-lists{
    margin-top:30px;
  }
  .question-lists li{
    display: flex;
